// src/App.jsx
import React from 'react';
import { BrowserRouter as Router, Routes, Route, useParams, Navigate } from 'react-router-dom';
import Navbar from './components/Navbar';
import MyLessons from './components/MyLessons';
import AllLessons from './components/AllLessons';
import Messages from './components/Messages';
import Profile from './components/Profile';
import Favorites from './components/Favorites';
import Programs from './components/Programs';
import RoleSelector from './components/RoleSelector';
import InstructorsList from './components/InstructorsList';
import TraineesList from './components/TraineesList';

const roles = ["trainee", "instructor", "secretary"];

const RoleRoutes = () => {
  const { role } = useParams();

  if (!roles.includes(role)) {
    return <Navigate to="/" />;
  }

  return (
    <>
      <Navbar username="User" role={role} />
      <Routes>
        <Route path={`${role}/my-lessons`} element={<MyLessons />} />
        <Route path={`${role}/all-lessons`} element={<AllLessons />} />
        <Route path={`${role}/messages`} element={<Messages />} />
        <Route path={`${role}/profile`} element={<Profile />} />
        {role === "trainee" && (
          <>
            <Route path={`${role}/favorites`} element={<Favorites />} />
            <Route path={`${role}/programs`} element={<Programs />} />
          </>
        )}
        {role === "secretary" && (
          <>
            <Route path={`${role}/all-instructors`} element={<InstructorsList />} />
            <Route path={`${role}/all-trainees`} element={<TraineesList />} />
          </>
        )}
        <Route path={`${role}/logout`} element={<h2>Logged out</h2>} />
      </Routes>
    </>
  );
};

const App = () => {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<RoleSelector roles={roles} onSelect={(role) => window.location.href = `/${role}/my-lessons`} />} />
        <Route path="/:role/*" element={<RoleRoutes />} />
      </Routes>
    </Router>
  );
};

export default App;
