// src/App.jsx
import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Routes, Route, useSearchParams, useNavigate } from 'react-router-dom';
import Navbar from './components/Navbar';
import MyLessons from './components/MyLessons';
import AllLessons from './components/AllLessons';
import Messages from './components/Messages';
import Profile from './components/Profile';
import Favorites from './components/Favorites';
import Programs from './components/Programs';
import RoleSelector from './components/RoleSelector';
import InstructorsList from './components/InstructorsList';
import TraineesList from './components/TraineesList';

const AppContent = () => {
  const [searchParams, setSearchParams] = useSearchParams();
  const role = searchParams.get("role");
  const navigate = useNavigate();

  const [roles] = useState(["trainee", "instructor", "secretary"]);

  useEffect(() => {
    if (!role) {
      // אם לא נבחר תפקיד עדיין, מחכים שהמשתמש יבחר ונעדכן את ה־URL
    } else if (!roles.includes(role)) {
      // אם הוזן תפקיד לא חוקי ב־URL, שולחים חזרה לבחירה
      navigate("/", { replace: true });
    }
  }, [role, roles, navigate]);

  if (!role || !roles.includes(role)) {
    return (
      <RoleSelector
        roles={roles}
        onSelect={(selectedRole) => {
          setSearchParams({ role: selectedRole });
        }}
      />
    );
  }

  return (
    <>
      <Navbar username="User" role={role} />
      <Routes>
        <Route path="/my-lessons" element={<MyLessons />} />
        <Route path="/all-lessons" element={<AllLessons />} />
        <Route path="/messages" element={<Messages />} />
        <Route path="/profile" element={<Profile />} />
        {role === "trainee" && (
          <>
            <Route path="/favorites" element={<Favorites />} />
            <Route path="/programs" element={<Programs />} />
          </>
        )}
        {role === "secretary" && (
          <>
            <Route path="/all-instructors" element={<InstructorsList />} />
            <Route path="/all-trainees" element={<TraineesList />} />
          </>
        )}
        <Route path="/logout" element={<h2>Logged out</h2>} />
      </Routes>
    </>
  );
};

const App = () => (
  <Router>
    <AppContent />
  </Router>
);

export default App;
